mapper.select:
select project_id projectId,count(model_id) modeNum from data_model where project_id in
#{item}
GROUP BY project_id

SELECT component_id as componentId, model_id as modelId, component_code as componentCode, component_name as componentName, shape, width, height, coordinate_y as coordinateY, data_json as dataJson, component_state as componentState, start_time as startTime, end_time as endTime FROM data_component
and model_id = #{modelId}
and component_code = #{componentCode}
and task_id = #{taskId}

select model_id as modelId, component_json as componentJson,
       model_desc as modelDesc,model_desc as modelDesc
from data_model where


            model_id = #{modelId}


            user_id = #{userId}

                and is_draft = #{isDraft}


                and project_id = #{projectId}



LIMIT 1

select DISTINCT dm.project_id projectId,dt.task_state latestTaskStatus,count(dt.task_id) statusCount from data_model_task dmt,data_task dt,data_model dm where dmt.task_id = dt.task_id and dm.model_id = dmt.model_id and dm.is_del = 0 and dm.project_id in
#{item}
GROUP BY dm.project_id,dt.task_state

select mt.model_id modelId,mt.task_id taskId,dt.task_state taskState,dt.task_id_name taskIdName,dt.task_start_time taskStartTime,dt.task_end_time taskEndTime from ( select DISTINCT model_id,max(task_id) task_id from data_model_task where model_id in
#{item}
GROUP BY model_id ) mt,data_task dt where mt.task_id = dt.task_id

select
mt.model_id modelId,mt.task_id taskId,dt.task_state taskState,dt.task_id_name taskIdName,dt.task_start_time taskStartTime,dt.task_end_time taskEndTime
from (
select
DISTINCT model_id,task_id
from data_model_task
where model_id in

    #{item}

) mt,data_task dt
where mt.task_id = dt.task_id
