mapper.sql:
where
dpt.task_id = dt.task_id
 and dpt.resource_name like CONCAT('%',#{resourceName,jdbcType=VARCHAR},'%')
 and dt.task_id_name like CONCAT('%',#{taskId,jdbcType=VARCHAR},'%')
 and dpt.provider_organ_name like CONCAT('%',#{organName,jdbcType=VARCHAR},'%')
 and dpt.retrieval_id like CONCAT('%',#{retrievalId,jdbcType=VARCHAR},'%')
 and dt.task_state = #{taskState}
 and dt.task_name like CONCAT('%',#{taskName,jdbcType=VARCHAR},'%')
and dpt.create_date >=#{startDate} 
and dpt.create_date <=#{endDate}

where is_del = 0

    and task_id_name = #{taskId}


    and task_start_time >= #{start}


    and task_end_time <= #{end}


    and task_name like CONCAT('%',#{taskName,jdbcType=VARCHAR},'%')


    and task_type = #{taskType}
